// === 动态 .saic 网格布局 (基于容器宽度 & 自身宽度设定) ===
// ========================================================
function handleDynamicSaicLayout() {
    const saicContainers = document.querySelectorAll('.saic');
    if (saicContainers.length === 0) return;

    const cardWidth = 150; // 每张卡片的固定宽度
    const gap = 1;         // 卡片间的缝隙

    saicContainers.forEach(container => {
        // --- 每次重新计算前，必须移除之前添加的所有占位符 ---
        const existingPlaceholders = container.querySelectorAll('.saic-placeholder');
        existingPlaceholders.forEach(p => p.remove());

        // 1. 核心修改：获取容器“当前可用”的宽度
        //    我们先临时将容器宽度设为100%，让它撑满父元素，然后再读取它的实际宽度
        container.style.width = '100%';
        const availableWidth = container.clientWidth; // clientWidth 是最可靠的宽度值

        // 2. 根据可用宽度，动态计算每行最多能放几张卡片
        let perRow = Math.floor((availableWidth + gap) / (cardWidth + gap));

        // 安全措施：确保每行至少有1张卡片
        if (perRow < 1) {
            perRow = 1;
        }

        // 3. 核心修改：根据 perRow 动态计算并设定 .saic 容器自身的“理想宽度”
        const targetSaicWidth = perRow * cardWidth + (perRow - 1) * gap;
        container.style.width = targetSaicWidth + 'px';

        // 4. 计算并用纯白卡片填满最后一行
        const originalCardsCount = container.children.length;
        const remainder = originalCardsCount % perRow;

        if (remainder !== 0) {
            const placeholdersToCreate = perRow - remainder;
            for (let i = 0; i < placeholdersToCreate; i++) {
                const placeholder = document.createElement('div');
                placeholder.className = 'saic-placeholder';
                placeholder.style.width = cardWidth + 'px';
                placeholder.style.background = 'white';
                placeholder.style.flexShrink = '0';
                container.appendChild(placeholder);
            }
        }
    });
}

// 事件监听器保持不变
window.addEventListener('DOMContentLoaded', handleDynamicSaicLayout);
let resizeTimer;
window.addEventListener('resize', () => {
    clearTimeout(resizeTimer);
    resizeTimer = setTimeout(handleDynamicSaicLayout, 100);
});



function AOE2_applyUnitCivDisable(funcName) {
    let tries = 0;
    const MAX_TRIES = 50; // 最多等 ~2.5 秒

    function tryApply() {
        tries++;

        // 1. 规则函数是否已存在
        const fn = window[funcName];
        if (typeof fn !== "function") {
            if (tries < MAX_TRIES) {
                return setTimeout(tryApply, 50);
            }
            return;
        }

        // 2. 解析函数体
        const src = fn.toString();
        const re = /getElementById\("([A-Za-z]+)2"\)\.classList\.add\("aoeTechIconOff"\)/g;

        let m;
        let applied = false;

        while ((m = re.exec(src))) {
            const civ = m[1];
            const el = document.getElementById(civ);

            // 3. DOM 还没进来，继续等
            if (!el) {
                if (tries < MAX_TRIES) {
                    return setTimeout(tryApply, 50);
                }
                return;
            }

            el.classList.add("aoeTechIconOff");

            applied = true;
        }

        // 如果这次什么都没应用，也不用再等
        if (!applied) return;
    }

    // 立即尝试一次
    tryApply();
}

/**
 * [已改造 v2] 为指定容器内的文明图标添加快速跳转链接
 * - 新增：会将当前页面的锚点(#hash)传递到新生成的链接中
 * @param {HTMLElement} [container=document] 要操作的父容器，默认为整个文档
 */
function AOE2_enableCivIconQuickJump(container) {
    // 如果没有提供容器，则默认为 document，以兼容旧的调用方式
    const scope = container || document;

    // [新增] 在函数开头，一次性获取当前页面的锚点 (#)
    // 如果URL是 ".../图皮.html#tgc", aoe2Hash就会是 "#tgc"
    // 如果URL没有锚点，aoe2Hash会是空字符串 ""
    const aoe2Hash = window.location.hash;

    scope.querySelectorAll('img[id$="2"][title]').forEach(img => {

        // 已经被包过就跳过
        if (img.closest('a')) return;

        const title = img.getAttribute("title");
        if (!title) return;

        const link = document.createElement("a");

        // [修改] 构造基础链接
        let targetHref = `https://seicing.com/html/aoe2/2/${title}.html`;

        // [修改] 如果存在锚点，就把它追加到链接后面
        if (aoe2Hash) {
            targetHref += aoe2Hash;
        }

        // [修改] 应用最终构建好的链接
        link.href = targetHref;

        link.style.color = "#0010ff";
        link.style.display = "inline-block";

        img.parentNode.insertBefore(link, img);
        link.appendChild(img);
    });
}

/**
 * [已改造] 在指定容器内高亮当前页面的文明图标
 * @param {HTMLElement} [container=document] 要操作的父容器，默认为整个文档
 */
function AOE2_activateCurrentCivIcon(container) {
    const path = location.pathname;
    if (!path.includes("/html/aoe2/")) return;

    // 如果没有提供容器，则默认为 document
    const scope = container || document;

    const fileName = path.split("/").pop();
    if (!fileName) return;

    const civName = decodeURIComponent(
        fileName.replace(/\.html$/i, "")
    ).trim();

    // 在指定的容器(scope)内查找 technoseigine
    const techno = scope.querySelector("#technoseigine");
    if (!techno) { // 修正了变量名 technoseigine -> techno
        // 在移动端克隆后，techno 容器肯定存在，如果在这里找不到，说明逻辑有问题。
        // console.warn("在指定容器内未找到 #technoseigine 元素");
        return;
    }

    let targetImg = null;

    // 在 techno 容器内查找所有图标
    techno.querySelectorAll("img[title]").forEach(img => {
        img.classList.add("civ-active936");

        const title = img.getAttribute("title").trim();
        if (title.toLowerCase() === civName.toLowerCase()) { // 增加 toLowerCase() 以增强匹配鲁棒性
            targetImg = img;
        }
    });

    if (!targetImg) {
        console.warn("在当前容器内未找到文明图标：", civName);
        return;
    }

    // 清理旧激活（仅在当前 techno 容器内）
    techno.querySelectorAll(".civ-active937").forEach(el => {
        el.classList.remove("civ-active937");
    });

    // 激活目标
    targetImg.classList.remove("civ-active936");
    targetImg.classList.add("civ-active937");
}



