function showPic(e, taitou) {
    var x, y, aasb;
    x = e.pageX;
    y = e.pageY;

    aasb = document.getElementById("Layer1");
    aasb.style.display = "";
    aasb.style.width = "210px";
    aasb.innerHTML = "<div style='background:rgba(0,0,0,0.75);padding:5px'><font style='color:#ffffff'>" + ayanami[taitou] + "</font></div>";
    var div = aasb;
    var z = div.offsetWidth;

    if (x + z < document.body.clientWidth) {
        aasb.style.left = x + 2 + 'px';
        aasb.style.top = y + 2 + 'px';
    } else {
        aasb.style.left = x - (x + z - document.body.clientWidth) + 'px';
        aasb.style.top = y + 2 + 'px';
    }

}

function hiddenPic() {
    var aasb = document.getElementById("Layer1");
    aasb.style.display = "none";
    aasb.innerHTML = "";
}

function aoetechPoe(a) {
    var aoetech = ayanami[a].lastIndexOf("<br>");
    var aoetechP = ayanami[a].substr(aoetech + 4);
    if ($("." + a).length > 0) {
        var x = document.getElementsByClassName(a);
        var i;
        for (i = 0; i < x.length; i++) {
            x[i].innerHTML = aoetechP;
        }
    } else { }
}

function aoetechPoeRush() {
    for (let key in ayanami) {
        var gasdss = key.toString();
        aoetechPoe(gasdss)
    }
}


document.addEventListener('DOMContentLoaded', () => {
    const containers = document.querySelectorAll('.saic');
    const cardWidth = 150;   // 卡片宽度
    const gap = 1;           // flex gap

    containers.forEach(container => {
        const perRow = parseInt(container.dataset.perRow) || 5; // 支持自定义
        const cards = container.children.length;
        const remainder = cards % perRow;

        if (remainder !== 0) {
            const remainingWidth = (perRow - remainder) * (cardWidth + gap) - gap;

            const placeholder = document.createElement('div');
            placeholder.style.width = remainingWidth + 'px';
            placeholder.style.background = 'white';  // 或者 transparent
            placeholder.style.flexShrink = '0';

            container.appendChild(placeholder);
        }
    });
});